import { prisma } from "@/app/utils/prisma";
import { NextResponse } from "next/server";
import jsPDF from "jspdf";
import Logo from "@/public/Logo.png";
import { addDays, format } from "date-fns";
import { formatCurrency } from "@/app/utils/formatCurrency";
export async function GET(
  request: Request,
  { params }: { params: Promise<{ invoiceId: string }> }
) {
  const { invoiceId } = await params;
  const data = await prisma.invoice.findUnique({
    where: {
      id: invoiceId,
    },
    select: {
      invoiceName: true,
      currency: true,
      clientName: true,
      invoiceNumber: true,
      dueDate: true,
      date: true,
      total: true,
      fromAddress: true,
      fromEmail: true,
      fromName: true,
      clientAddress: true,
      clientEmail: true,
      invoiceItemDescription: true,
      invoiceItemQuantity: true,
      invoiceItemRate: true,
      notes: true,
    },
  });

  if (!data) {
    return NextResponse.json({ error: "Invoice not found" }, { status: 404 });
  }

  const pdf = new jsPDF({
    orientation: "portrait",
    unit: "mm",
    format: "a4",
    putOnlyUsedFonts: true,
    floatPrecision: 16,
  });

  //set font
  pdf.setFont("helvetica", "normal");

  //set header
  pdf.setFontSize(24);
  pdf.text("Invoice", 105, 20, { align: "center" });

  //From section
  // pdf.setFontSize(12);
  // pdf.text("From", 20, 40);
  // pdf.setFontSize(10);
  // pdf.text(data.fromName, 20, 50);
  // pdf.text(data.fromAddress, 20, 60);
  // pdf.text(data.fromEmail, 20, 70);
  pdf.setFontSize(12);
  pdf.text("From", 20, 40);
  pdf.setFontSize(10);
  pdf.text([data.fromName, data.fromEmail, data.fromAddress], 20, 45);

  //Client section
  pdf.setFontSize(12);
  pdf.text("Bill To", 150, 40);
  pdf.setFontSize(10);
  pdf.text([data.clientName, data.clientEmail, data.clientAddress], 150, 45);

  const finalDate = addDays(data.date, data.dueDate);
  //Invoice details
  pdf.setFontSize(12);
  pdf.text("Invoice Details", 20, 80);
  pdf.setFontSize(10);
  pdf.text(`Invoice Number: #${data.invoiceNumber}`, 20, 85);
  pdf.text(`Date:   ${format(data.date, "PP")}`, 20, 90);
  pdf.text(`Due Date:  ${format(finalDate, "PP")}`, 20, 95);

  //a line
  // pdf.setFont("helvetica", "bold");

  pdf.line(14, 110, 196, 110); // from x=14 to x=196 at y=92

  //Item table header
  pdf.setFontSize(10);
  pdf.setFont("helvetica", "bold");
  pdf.text("Description", 20, 120);
  pdf.text("Quantity", 100, 120);
  pdf.text("Rate", 140, 120);
  pdf.text("Total", 160, 120);
  pdf.line(14, 122, 196, 122); // from x=14 to x=196 at y=92

  //Item Details
  pdf.setFont("helvetica", "normal");
  pdf.text(`${data.invoiceItemDescription}`, 20, 130);
  pdf.text(`${data.invoiceItemQuantity}`, 100, 130);
  pdf.text(
    `${formatCurrency({
      amount: data.invoiceItemRate,
      currency: data.currency as any,
    })}`,
    140,
    130
  );
  pdf.text(
    `${formatCurrency({
      amount: data.total,
      currency: data.currency as any,
    })}`,
    160,
    130
  );

  pdf.line(14, 135, 196, 135);
  pdf.setFont("helvetica", "normal");
  pdf.text(`Total ${data.currency}`, 140, 145);
  pdf.text(
    `${formatCurrency({
      amount: data.invoiceItemRate,
      currency: data.currency as any,
    })}`,
    160,
    145
  );

  //Addition Notes

  if (data.notes) {
    pdf.setFont("helvetica", "normal");
    pdf.setFontSize(10);
    pdf.text(`NOTE:`, 20, 165);
    pdf.text(data.notes, 20, 170);
  }

  //generate pdf as Buffer
  const pdfBuffer = Buffer.from(pdf.output("arraybuffer"));
  //Buffer.from converts the pdf to a buffer object of node.js
  //and the output is in arraybuffer format
  //so we can convert it to a buffer object
  //and send it as a response

  return new NextResponse(pdfBuffer, {
    status: 200,
    headers: {
      "Content-Type": "application/pdf",
      //apllication/pdf is the content type for pdf files
      "Content-Disposition": "inline",
      //content-disposition is used to specify how the content should be displayed
      //inline means the content should be displayed in the browser
    },
  });
}

